import { getSession } from '@/lib/auth';

export default async function AdminDashboard() {
  const session = await getSession();

  const stats = [
    { label: 'Total Users', value: 128 },
    { label: 'Total Items', value: 456 },
    { label: 'Recent Activity', value: 34 },
